INSERT INTO `hol-cas` (`ide`, `nom`, `des`, `lec`, `arm`, `ond`, `pos_arm`, `ton`, `ton_arm`) VALUES
(01, 'Rojo Magnético', 'Inicia el Tiempo atrayendo el Propósito para Establecer la Base de la Acción.', '\"El único propósito de la vida es recordar a Dios, y retornar al alma, ese lugar \"', 1, 1, 1, 01, 01),
(02, 'Blanco Lunar', 'Refina la Vida Orgánica estabilizando el Desafío para Establecer la Base de la Acción.', '\"Deshacer la nube que cubre el sistema materialista de creencias, es el propósito de la Historia Cósmica, sólo entonces puede brillar claro el campo mental del humano como el campo mental planetario.\"', 1, 1, 2, 02, 01),
(03, 'Azul Eléctrico', 'Transforma el Sentir-Sentidos vinculando el Servicio para Establecer la Base de la Acción.', '\"El telar de la realidad es sagrado, y hay una autoría Divina del universo – el alma existe con la inscripción de Dios que dice: Recuérdame.\"', 1, 1, 3, 03, 01),
(04, 'Amarillo Autoexistente', 'Madura la Mente midiendo la Forma para Establecer la Base de la Acción.', '\"El primer paso para cambiar a un estado cósmico de la mente, es a través del uso diario del calendario de 13 Lunas de 28 días, esta es la base y el fundamento de la nueva estructura del mundo.\"', 1, 1, 4, 04, 01),
(05, 'Rojo Entonado', 'Inicia el Tiempo comandando la Radiancia para Extender el Ritmo de la Acción.', '\"El programa del calendario de 13 Lunas es un paso radical, que es el propósito de la Historia Cósmica: conducir al alma de regreso a la perfección divina del orden del universo, como lo que realmente es.\"', 1, 2, 1, 05, 02),
(06, 'Blanco Rítmico', 'Refina la Vida Orgánica equilibrando la Igualdad para Extender el Ritmo de la Acción.', '\"El calendario de 13 Lunas/28 días no es sólo un vehículo para la rearmonización de la mente humana, y finalmente del ADN humano, sino que también es una herramienta para la trascendencia instantánea de la historia.\"', 1, 2, 2, 06, 02),
(07, 'Azul Resonante', 'Transforma el Sentir-Sentidos inspirando la Sintonización para Extender el Ritmo de la Acción.', '\"La aplicación de las Crónicas de la Historia Cósmica con los códigos del tiempo te abre a los niveles multidimensionales de la consciencia, y una vez que tu mente está realmente operando en estos códigos, empezarás a experimentar niveles cada vez más elevados de teleportación sensorial.\"', 1, 2, 3, 07, 02),
(08, 'Amarillo Galáctico', 'Madura la Mente modelando la Integridad para Extender el Ritmo de la Acción.', '\"La Historia Cósmica es un sistema de pensamiento y una técnica que debe aprenderse y aplicarse para que el humano pueda dar los siguientes pasos en el camino de la evolución hacia un sistema perceptual holográfico.\"', 1, 2, 4, 08, 02),
(09, 'Rojo Solar', 'Inicia el Tiempo realizando la Intención para Convertir la Acción.', '\"La Historia Cósmica es el nivel más elevado de información y comprensión por lo que la historia previa queda subordinada, transformada, y rehecha\"', 1, 3, 1, 09, 03),
(10, 'Blanco Planetario', 'Refina la Vida Orgánica produciendo la Manifestación para Convertir la Acción.', '\"Todas las percepciones que has acumulado deben limpiarse a la luz de la verdad de que sólo hay una tradición, una religión, una Tierra, y un ser.\"', 1, 3, 2, 10, 03),
(11, 'Azul Espectral', 'Transforma el Sentir-Sentidos divulgando la Liberación para Convertir la Acción.', '\"No puede haber ningún avance nuevo dentro de la matriz de una vieja estructura sin un sacrificio – tienes que sacrificar algo de ti mismo – si no es todo lo tuyo que está apegado o involucrado en lo viejo, para conseguir lo nuevo.\"', 1, 3, 3, 11, 03),
(12, 'Amarillo Cristal', 'Madura la Mente universalizando la Cooperación para Convertir la Acción.', '\"La disolución de las viejas estructuras, así como el cultivo de la compasión universal hacia todos los seres, es la meta de la evolución del humano planetario.\"', 1, 3, 4, 12, 03),
(13, 'Rojo Cósmico', 'Inicia el Tiempo trascendiendo la Presencia para Transportar la Acción.', '\"De acuerdo a la Historia Cósmica, el yoga existe como un sistema necesario para facilitar esta siguiente etapa de transformación, donde la mente y el alma humana experimentan la unificación en un nivel noosférico o planetario.\"', 1, 4, 1, 13, 04),
(14, 'Blanco Magnético', 'Refina el Tiempo atrayendo el Propósito para Establecer la Base de la Acción.', '\"El yoga es la herramienta básica para construir la base del templo interior, que penetra a través de lo profano para que puedas realizar tu innata identidad cósmica.\"', 2, 1, 2, 01, 04),
(15, 'Azul Lunar', 'Transforma la Vida Orgánica estabilizando el Desafío para Establecer la Base de la Acción.', '\"El yoga, como sistema de conocimiento, empieza con el cuerpo físico y penetra hasta los más profundos rincones de la mente cósmica universal.\"', 2, 1, 3, 02, 04),
(16, 'Amarillo Eléctrico', 'Madura el Sentir-Sentidos vinculando el Servicio para Establecer la Base de la Acción.', '\"La totalidad de lo que necesita ser conocido está contenido dentro del cuerpo humano, cuando esto sea entendido, entonces también se llegarán a conocer los medios paranormales para extender los sentidos hacia los órdenes de la naturaleza.\"', 2, 1, 4, 03, 04),
(17, 'Rojo Autoexistente', 'Inicia la Mente midiendo la Forma para Establecer la Base de la Acción.', '\"Desde el punto de vista del hechicero, el propósito del yoga es establecer el cuerpo como una base y un templo completamente unificados, de modo que el reflejo y el orden de los movimientos del cuerpo sean un reflejo del orden sagrado original del cosmos superior.\"', 2, 1, 1, 04, 05),
(18, 'Blanco Entonado', 'Refina el Tiempo comandando la Radiancia para Extender el Ritmo de la Acción.', '\"La Historia Cósmica es la estructura del universo proyectada a través de tu propio cuerpo y ser como una armoniosa obra de arte.\"', 2, 2, 2, 05, 05),
(19, 'Azul Rítmico', 'Transforma la Vida Orgánica equilibrando la Igualdad para Extender el Ritmo de la Acción.', '\"Debes entender que lo que sacas al mundo y lo que interpretas no es más que tu propia retroalimentación psicogenética.\"', 2, 2, 3, 06, 05),
(20, 'Amarillo Resonante', 'Madura el Sentir-Sentidos inspirando la Sintonización para Extender el Ritmo de la Acción.', '\"La Historia Cósmica es una revolución interna que depende del cultivo de la visión.\"', 2, 2, 4, 07, 05),
(21, 'Rojo Galáctico', 'Inicia la Mente modelando la Integridad para Extender el Ritmo de la Acción.', '\"Todo el trabajo que estás haciendo es para retornar a este punto de Dios en el centro del círculo donde la luz es siempre pura.\"', 2, 2, 1, 08, 06),
(22, 'Blanco Solar', 'Refina el Tiempo realizando la Intención para Convertir la Acción.', '\"El propósito de la creación del ser humano es crear un medio altamente evolucionado y complejo, para que no sólo actúe como un vehículo de la inteligencia cósmica, sino como un conducto para la consciencia y el espíritu en constante evolución.\"', 2, 3, 2, 09, 06),
(23, 'Azul Planetario', 'Transforma la Vida Orgánica produciendo la Manifestación para Convertir la Acción.', '\"Desde el punto de vista de la Ciencia Cósmica, quien tú realmente eres es el componente evolutivo que mantiene el elemento pensante intencional del cosmos.\"', 2, 3, 3, 10, 06),
(24, 'Amarillo Espectral', 'Madura el Sentir-Sentidos divulgando la Liberación para Convertir la Acción.', '\"El arte es la manera en que el ser cuatridimensional utiliza el cuerpo tridimensional, para ponerlo en armonía con el quintidimensional.\"', 2, 3, 4, 11, 06),
(25, 'Rojo Cristal', 'Inicia la Mente universalizando la Cooperación para Convertir la Acción.', '\"Al ser consciente de los tres universos: fenomenal, imaginal y moral, que siempre están operando simultáneamente, puedes comenzar a crear una evaluación ampliada de quién eres, y que es lo que realmente estás haciendo aquí.\"', 2, 3, 1, 12, 07),
(26, 'Blanco Cósmico', 'Refina el Tiempo trascendiendo la Presencia para Transportar la Acción.', '\"Debes empezar a examinar el proceso de todo. Todo lo que existe está basado en algún tipo de patrón divino.\"', 2, 4, 2, 13, 07),
(27, 'Azul Magnético', 'Transforma el Tiempo atrayendo el Propósito para Establecer la Base de la Acción.', '\"La manera sencilla y más elegante siempre será verdadera, por lo tanto, será estética y moral. La verdad siempre es armónica.\"', 3, 1, 3, 01, 07),
(28, 'Amarillo Lunar', 'Madura la Vida Orgánica estabilizando el Desafío para Establecer la Base de la Acción.', '\"El sufrimiento existe porque has elegido creer en la ilusión. El universo moral indica elección. Tú siempre tienes la oportunidad de elegir el camino correcto o el camino incorrecto.\"', 3, 1, 4, 02, 07),
(29, 'Rojo Eléctrico', 'Inicia el Sentir-Sentidos vinculando el Servicio para Establecer la Base de la Acción.', '\"Todo lo que crece está destinado a trascenderse a sí mismo. Cuando te conviertes plenamente en quien tú eres, tu personaje se abre como una flor, cuando floreces completamente, pasas más allá de ti mismo hacia la imagen final de la flor, que es el desarrollo completo del alma.\"', 3, 1, 1, 03, 08),
(30, 'Blanco Autoexistente', 'Refina la Mente midiendo la Forma para Establecer la Base de la Acción.', '\"El arte de UR (recuerdo universal) es el arte que está destinado a invocar a la memoria cósmica como un todo cósmico y a la memoria del ser como el cosmos entero y la historia entera del cosmos.\"', 3, 1, 2, 04, 08),
(31, 'Azul Entonado', 'Transforma el Tiempo comandando la Radiancia para Extender el Ritmo de la Acción.', '\"El proceso de redención significa dejar de ser ignorantes, o empezar a recordar o rememorar. Recuerdo Universal significa que los humanos ya no son ignorantes ni de su origen ni su destino.\"', 3, 2, 3, 05, 08),
(32, 'Amarillo Rítmico', 'Madura la Vida Orgánica equilibrando la Igualdad para Extender el Ritmo de la Acción.', '\"El recuerdo holográfico es el despliegue de un ser en un orden planetario, y empieza una vez que la frecuencia humana cambia al cambiar el calendario.\"', 3, 2, 4, 06, 08),
(33, 'Rojo Resonante', 'Inicia el Sentir-Sentidos inspirando la Sintonización para Extender el Ritmo de la Acción.', '\"Sólo cuando la voluntad personal está rendida a la Voluntad Divina, tendremos la oportunidad de crear un final victorioso a la película que está siendo proyectada en la tercera dimensión.\"', 3, 2, 1, 07, 09),
(34, 'Blanco Galáctico', 'Refina la Mente modelando la Integridad para Extender el Ritmo de la Acción.', '\"El viaje interplanetario comienza con la meditación que despeja la mente de la tendencia a pensar, esto es obligatorio en el desarrollo del samadhi y la concentración en el samadhi, para ir donde tengas que ir o hacer lo que tenga que hacerse.\"', 3, 2, 2, 08, 09),
(35, 'Azul Solar', 'Transforma el Tiempo realizando la Intención para Convertir la Acción.', '\"Al ver toda la película de tu vida, la calidad de lo que experimentaste y cómo respondiste a esas experiencias condiciona lo que va a sucederte en la siguiente vida o el Más Allá.\"', 3, 3, 3, 09, 09),
(36, 'Amarillo Planetario', 'Madura la Vida Orgánica produciendo la Manifestación para Convertir la Acción.', '\"Todos los archivos de la película de cada ser que ha existido, están almacenados en la cuarta dimensión, que es el espacio de la imaginación o reino imaginal.\"', 3, 3, 4, 10, 09),
(37, 'Rojo Espectral', 'Inicia el Sentir-Sentidos divulgando la Liberación para Convertir la Acción.', '\"El grado en el que estamos inmersos en nuestro ego, es el grado en el que estamos creando y repitiendo clichés noosféricos. El grado al que estamos abiertos al espíritu y a las formas de vida y comunicación no egóicas, a ese grado nos estamos potencializando como medios noosféricos.\"', 3, 3, 1, 11, 10),
(38, 'Blanco Cristal', 'Refina la Mente universalizando la Cooperación para Convertir la Acción.', '\"Los códigos de la Ley del Tiempo existen para ayudarnos a mantener la consciencia continua en la frecuencia de tiempo 13:20, y accesar así la noosfera.\"', 3, 3, 2, 12, 10),
(39, 'Azul Cósmico', 'Transforma el Tiempo trascendiendo la Presencia para Transportar la Acción.', '\"La noosfera está ahora absorbiendo en su interior la mente y la consciencia de un número cada vez mayor de gente.\"', 3, 4, 3, 13, 10),
(40, 'Amarillo Magnético', 'Madura el Tiempo atrayendo el Propósito para Establecer la Base de la Acción.', '\"La autosuficiencia espiritual es una función de disciplina que es un sacrificio espiritual continuo del ser inferior en aras de la perfección o florecer interno y pulido de lo divino o forma interna encarnada en el ser.\"', 4, 1, 4, 01, 10),
(41, 'Rojo Lunar', 'Inicia la Vida Orgánica estabilizando el Desafío para Establecer la Base de la Acción.', '\"Para facilitar el mayor ajuste a la consciencia humana involucrada en la activación de la noosfera, es necesario cultivar la paranormalidad multidimensional.\"', 4, 1, 1, 02, 11),
(42, 'Blanco Eléctrico', 'Refina el Sentir-Sentidos vinculando el Servicio para Establecer la Base de la Acción.', '\"Para adquirir poderes paranormales se requiere esfuerzo, disciplina de la mente y empeño, e incluso entonces, el empeño no es para adquirir poderes, sino para lograr mi propia maestría o realización del ser que es la meta del yoga y la meditación.\"', 4, 1, 2, 03, 11),
(43, 'Azul Autoexistente', 'Transforma la Mente midiendo la Forma para Establecer la Base de la Acción.', '\"Aprender a coordinar el ser tridimensional con el ser cuatridimensional, es un propósito principal de vida, y nos abre a la paranormalidad multidimensional, que es en realidad la norma.\"', 4, 1, 3, 04, 11),
(44, 'Amarillo Entonado', 'Madura el Tiempo comandando la Radiancia para Extender el Ritmo de la Acción.', '\"Tu identificación debe cambiarse a la de un hechicero planetario, que cultiva sus poderes paranormales multidimensionales para ayudar en la evolución supermental del planeta. No existe nada más.\"', 4, 2, 4, 05, 11),
(45, 'Rojo Rítmico', 'Inicia la Vida Orgánica equilibrando la Igualdad para Extender el Ritmo de la Acción.', '\"Nuestra tarea es hacer consciente el alma del mundo, iniciándola en el orden sincrónico de la realidad de cada día, de modo que los seres humanos sean estabilizados en un nivel normativo superior de la consciencia continua.\"', 4, 2, 1, 06, 12),
(46, 'Blanco Resonante', 'Refina el Sentir-Sentidos inspirando la Sintonización para Extender el Ritmo de la Acción.', '\"Igual que la biosfera es el medio en la Tierra para la transformación de la energía cósmica, la noosfera es el medio para la transmutación y transformación de las formas-pensamiento cósmicas.\"', 4, 2, 2, 07, 12),
(47, 'Azul Galáctico', 'Transforma la Mente modelando la Integridad para Extender el Ritmo de la Acción.', '\"Un mago es alguien que está sintonizado con el conocimiento de los procesos de la naturaleza, de tal forma que él/ella puede coordinar mentalmente estos procesos con otros para crear ciertos efectos, los cuales, para la mayoría de la gente, son aparentemente imposibles.\"', 4, 2, 3, 08, 12),
(48, 'Amarillo Solar', 'Madura el Tiempo realizando la Intención para Convertir la Acción.', '\"La humanidad es el punto a través del cual evolucionan la manifestación y los comandos de la jerarquía y formas culturales de la matriz de Shambhalla/Tollan. Debemos formar imágenes claras acerca de cómo funciona todo esto.\"', 4, 3, 4, 09, 12),
(49, 'Rojo Planetario', 'Inicia la Vida Orgánica produciendo la Manifestación para Convertir la Acción.', '\"El estudio de las Crónicas de la Historia Cósmica es el primer paso para vislumbrar el mecanismo y la estructura de cómo está siendo reformulada toda la mente humana y la base del conocimiento humano.\"', 4, 3, 1, 10, 13),
(50, 'Blanco Espectral', 'Refina el Sentir-Sentidos divulgando la Liberación para Convertir la Acción.', '\"Las Crónicas de la Historia Cósmica son el poder del número siete, en el que subyace la narración de la creación cósmica y la creación del campo cósmico como la función de las diferentes etapas de la evolución del tiempo.\"', 4, 3, 2, 11, 13),
(51, 'Azul Cristal', 'Transforma la Mente universalizando la Cooperación para Convertir la Acción.', '\"Cuanto más se imbuye tu mente con el poder resonante informativo de la Historia Cósmica, más se convierte en un reflejo de la crónica primordial del siete.\"', 4, 3, 3, 12, 13),
(52, 'Amarillo Cósmico', 'Madura el Tiempo trascendiendo la Presencia para Transportar la Acción.', '\"La comprensión de las Crónicas de la Historia Cósmica como la estructura del orden del poder del número siete, es la serpiente que muerde su cola para liberar el veneno que sana hasta la completitud.\"', 4, 4, 4, 13, 13);
