INSERT INTO `hol-kin_arm_tra` (`ide`, `nom`, `des`, `tit`, `lec`, `fac`, `may`, `cel`, `kin`) VALUES
(01, 'Magnética', 'Unifica el propósito del giro galáctico atrayendo', 'la Implantación Estelar', 'Entrada de la Tierra en el Rayo de Sincronización Galáctica.; Implantación de las “transmisiones estelares” de la federación galáctica, entre los pueblos, de una parte a otra del planeta.; Consolidación del alto y bajo Egipto, en el año 3.100 A.C.; Expansión de Sumeria en el año 3.000 A.C.; La construcción de Stonehenge comenzó en el año 2.800 A.C.', '3.113 A.C. - 2.718 A.C.', '13.0.0.0.0', '01 - 05', '001 - 020'),
(02, 'Lunar', 'Polariza el desafío del giro galáctico estabilizando', 'la Pirámide', 'Construcción de la Gran Pirámide en Giza, Egipto, del año 2.700 al 2.600 A.C, marca el anclaje del cuerpo de luz planetario.; Expansión de la civilización Sumeria, Akkad y Ur, y desarrollo del bronce.; Comienzo de Harapa y de la civilización del valle del Indos.; Comienzo de la vida agrícola sedentaria en China, Mesoamérica, y los Andes.', '2.718 A.C. - 2-324 A.C.', '1.0.0.0.0', '06 - 10', '021 - 040'),
(03, 'Eléctrica', 'Activa el servicio del giro galáctico vinculando', 'la Rueda', 'Pleno establecimiento de la rueda, iniciación de la tecnología del transporte y del pensamiento cíclico, escritura de los códigos de la ley, y desarrollo de la tecnología metalúrgica en Mesopotamia.; Sargón y el primer imperio babilónico.; Comienza la construcción de carros de guerra, y se inicia el imperialismo territorial de los emperadores legendarios en China.; Establecimiento de la civilización Minoica en Creta.', '2.324 A.C. - 1.930 A.C.', '2.0.0.0.0', '11 - 15', '041 - 060'),
(04, 'Auto-existente', 'Define la forma del giro galáctico midiendo', 'la Montaña Sagrada', 'Surgen los reinos Medio y Nuevo en Egipto: la re-ubicación del centro en la Montaña Sagrada del Oeste, el Valle de los Reyes marca la decisión de los egipcios de perpetuar la regencia dinástica.; se consolida el modelo del territorialismo defensivo como una norma de la vida civilizada.; Vienen las oleadas de invasores - los Hititas y los Arios.; son destruidas la civilizaciones minoicas y la del Valle del Indos.', '1.930 A.C. - 1.536 A.C.', '3.0.0.0.0', '16 - 20', '061 - 080'),
(05, 'Entonada', 'Confiere poder al giro galáctico comandando', 'la Casa Shang', 'Establecimiento de la dinastía Shang en China, enunciación de la doctrina del Yin y el Yang, se presentan adelantos en la metalurgia del bronce y en el modelo de la civilización China.; Comienzos de la civilización védica en la India.; Aparición de la civilización Chavín en los Andes, y Olmeca en Mesoamérica.; Akhenatón en Egipto, y de Abraham y Moisés en Israel.; los Hititas se consolidan en Mesopotamia.', '1.536 A.C. - 1.141 A.C.', '4.0.0.0.0', '21 - 25', '081 - 100'),
(06, 'Rítmica', 'Organiza la igualdad del giro galáctico equilibrando', 'el Sello Imperial', 'Imperios Asirio y Babilónico: Se desarrollan el armamento metálico y las máquinas de guerra.; En el Mediterráneo surgen los griegos de Micenas, saqueo de Troya.; Dinastía Chou en China, aparición del I-Ching.; Expansión de la cultura Olmeca en toda Mesoamérica.; El caballo es utilizado para la guerra.; se conforma el modelo de regencia imperial militarista, y se establece la sucesión dinástica como norma para la vida civilizada en el planeta.', '1.141 A.C. - 747 A.C.', '5.0.0.0.0', '26 - 30', '101 - 120'),
(07, 'Resonante', 'Canaliza la sintonización del giro galáctico inspirando', 'las Enseñanzas Mentales', 'Periodo de la primera oleada de mayas galácticos en Mesoamérica.; El Imperio Persa.; Surge el pensamiento filosófico individualista, que suplanta a las primitivas formas colectivas.; En Grecia Pitágoras, Sócrates, Platón, y Aristóteles.; en la India: seis escuelas de pensamiento védico, Mahavira y Buddha.; en China Lao Tze, Confucio y Chuang Tzu.; Se realiza la construcción de Monte Albán en Méjico, inicios de los sistemas del calendario Maya.', '747 A.C. - 353 A.C.', '6.0.0.0.0', '31 - 35', '121 - 140'),
(08, 'Galáctica', 'Armoniza la integridad del giro galáctico modelando', 'el Ungido o Consagrado', 'La civilización helenística.; Alejandro el Grande: apogeo de Roma y comienzo del Imperio Romano.; los Celtas en Europa, tecnología avanzada del hierro.; consolidación de los estados guerreros de China por CHin Huang Ti, comienzos de la Dinastía Han, construcción de la Gran Muralla China.; difusión del budismo como religión cosmopolita, desde la India hasta el Asia Central.; Vida de Jesucristo, aparición de las religiones gnósticas del Medio Oriente.; difusión Olmeca y comienzo de Teotihuacán.', '353 A.C. - 41 D.C.', '7.0.0.0.0', '36 - 40', '141 - 160'),
(09, 'Solar', 'Pulsa la intención del giro galáctico realizando', 'los Señores de Rojo y de Negro', 'Termina la construcción de la pirámide de Teotihuacán, consolidación del régimen cultural mesoamericano, los señores del Rojo y el Negro, primeras enseñanzas de Quetzalcoatl.; Moche, Nazca, y Tiahuanaco en los Andes.; Isla de Pascua, aparición de los reinos del África Occidental.; expansión y caída del Imperio Romano, ascenso del cristianismo.; caída de la Dinastía Han, difusión del budismo en China y en el sureste asiático.', '41 D.C. - 435. D.C.', '8.0.0.0.0', '41 - 45', '161 - 180'),
(10, 'Planetaria', 'Perfecciona la manifestación del giro galáctico produciendo', 'la Era del Conocimiento Maya', 'Segunda visita de los mayas galácticos, Pacal Votan de Palenque, y florecimiento del régimen cultural maya.; Mahoma y aparición del Islam.; Europa Occidental Cristiana Romana y Europa Oriental Cristiana Bizantina Ortodoxa.; Surge el hinduismo en la India.; Expansión del budismo al Tibet, Corea, y Japón.; En China gobierna la dinastía Táng.; Se consolidan los reinos del suroeste asiático: Indonesia (Borobadur y Java).; Época esplendorosa de Tiahuanaco en los Andes: surge la civilización Polinesia en Oceanía.; Primer florecimiento de la civilización de Nigeria.', '435 D.C. - 830 D.C.', '9.0.0.0.0', '46 - 50', '181 - 200'),
(11, 'Espectral', 'Disuelve la liberación del giro galáctico divulgando', 'las Guerras Santas', 'Colapso de los Mayas Clásicos y de la civilización mejicana central, Quetzalcóatl año 1 Caña surgen los toltecas.; Las civilizaciones de Chan Chan y Chimu en los Andes.; Surge la I\'fe en Nigeria.; Florecimiento y difusión del Islam y su confrontación con la civilización cristiana - Las cruzadas.; Apogeo de la civilización tibetana.; Regencia de la dinastía Sung en China, invención de la imprenta y la pólvora.; Regencia de la Dinastía Khmer en el sur-este asiático.; Surge en África oriental la Gran Zimbabwe.', '830 D.C. - 1.224 D.C.', '10.0.0.0.0', '51 - 55', '201 - 220'),
(12, 'Cristal', 'Dedica la cooperación del giro galáctico universalizando', 'la Semilla Oculta', 'Expansión del Islam a la India, al Asia Central y Suroriental, y al África Occidental.; Aislamiento del Tibet.; Aislamiento del Japón.; Surgen los turcos y mongoles, conquista de China.              ; Florecimiento de Zimbabwe en el África oriental, I\'fe y Benin en África Occidental.; Llega al pináculo la civilización cristiana en Europa occidental y surge la civilización Ortodoxa Rusa en Europa oriental, reforma y división de la iglesia cristiana.; Expansión y triunfo de la civilización europea en la conquista de los Imperios Inca y Azteca.; Comienzo de la colonización europea, cae en decadencia la concepción ( visión ) sagrada del universo ( la semilla oculta ).', '1.224 D.C. - 1.618 D.C.', '11.0.0.0.0', '56 - 60', '221 - 240'),
(13, 'Cósmica', 'Perdula la presencia del giro galáctico trascendiendo', 'la Transformación de la Materia', 'Inicio y triunfo del materialismo científico, Europa conquista el mundo, la revolución industrial, las revoluciones democráticas en América, Europa.; Expansión del colonialismo al África, América Latina, y Asia.; Industrialización del Japón.; Karl Marx y la aparición del comunismo, estallan las revoluciones comunistas de Rusia y China.; Primera y segunda Guerra Mundial.; La bomba atómica y la era nuclear.; Surgen las potencias del Tercer Mundo, el Islam, Méjico y la India.; Auge del terrorismo global y colapso de la civilización tecnológica.; Purificación de la Tierra, y era final de la regeneración global.; Rra de la informática, desarrollo de la tecnología solar y de los cristales.; Sincronización galáctica.', '1.618 D.C. - 2.012 D.C.', '12.0.0.0.0', '61 - 65', '241 - 260');
